import { useState } from 'react'
import Notes from './components/Note'

const Header = (props) =>{
  return(
    <div>
      <h1>{props.course.name}</h1>
    </div>
  )

}

const Part =(props) =>{
  return(
    <div>{props.name} {props.exercises}</div>
  )
}

const Content = (props) =>{
  return(
    <div>
    {props.course.parts.map(part => <Part key={part.id} name={part.name} exercises={part.exercises}/>)}
    </div>
  )
}

const Total = (props) =>{
  const parts = props.course.parts.map(course => course.exercises)

  return (

    <div>
    <p>Total of {parts.reduce((s, p) => s + p)} exercises</p>
    </div>

  )
}

const Course = (props) =>{

    return(

      <div>

        <Header  course={props.courses}/>
        <Content course={props.courses}/>
        <Total course={props.courses}/>

      </div>
    )

  }

